import { socket } from './socket.js'; export function createTask(taskData) { socket.emit('task:create', taskData); } export function updateTask(taskId, updates) { socket.emit('task:update', { taskId, updates }); } export function deleteTask(taskId) { socket.emit('task:delete', { taskId }); } export function changeTaskStatus(taskId, newStatus) { socket.emit('task:update', { taskId, updates: { status: newStatus } }); } export function joinSession(userName) { socket.emit('user:join', { userName }); }